import { Component, HostListener, Input, OnInit} from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router} from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-editcv',
  template: `
  <h1 class="center">Modifier CV {{cv?.nom}}</h1>
  <p>{{cv?.titCV}}</p>
  <app-formmcv *ngIf="cv" [cv]="cv"></app-formmcv>
`,
})
export class EditcvComponent implements OnInit {
  iseditCv!: boolean;
   cv!: Cv|undefined ;
   cvForm!: FormGroup;
  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private cvService: CvService,
    private formBuilder: FormBuilder,
  )
  {
    this.iseditCv=this.router.url.includes('edit')
    this.cvForm = this.formBuilder.group({
      // photoCv: [''],
      nom: new FormControl((['', Validators.required]))  ,
      prenom: new FormControl(['', Validators.required]),
      email:  (['', [Validators.required, Validators.email]]),
      telephone:(['', [Validators.required, this.validateTelephoneFormat]]),
      titCV: (['', Validators.required]),
      profilCv: (['', Validators.required]),
      objecCarri: (['', [Validators.required]]),
    });
  }
  validateTelephoneFormat(control: AbstractControl): { [key: string]: boolean } | null {
    const phoneNumberPattern = /^\d{2}-\d{3}-\d{3}$/;
    if (control.value && !phoneNumberPattern.test(control.value)) {
      return { 'telephoneFormat': true };
    }
    return null;
  }  

  @HostListener('click', ['$event.target'])
  ngOnInit() {
    const cvId: string|null = this.route.snapshot.paramMap.get('id');
    if (this.iseditCv) {
    if (cvId) {
      this.cvService.get(+cvId).subscribe((data) => { this.cv = data
        console.log("le cv à modifier est ", this.cv)
      });
    }}}}
