import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-detailcv',
  templateUrl: './detailcv.component.html',
  styleUrls: ['./detailcv.component.css']
})

export class DetailcvComponent {
  cv!: Cv| undefined; 

  constructor(private route: ActivatedRoute, private cvService: CvService, private router: Router) {}

  ngOnInit() {
    const cvId: string|null= this.route.snapshot.paramMap.get('id');
    if(cvId){
      this.cvService.get(+cvId).subscribe({next:(cv) => {this.cv = cv}} );
    }} 

  goToEditCv(cv: Cv){
    this.router.navigate(['/edit', cv.id])
  }

  goToListCv(){
    this.router.navigate(['/list'])
  }

}
