import { Component, OnInit, HostListener} from '@angular/core';
import { ActivatedRoute, Router} from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-editcv',
  template: `
<h1 class="center">Modifier CV numéro:  {{cv?.id}} <br>  {{cv?.nom}} &nbsp;&nbsp;{{cv?.prenom}} </h1>
<p *ngIf="cv" class="center">{{cv.titCV}} <br> {{cv.email}} <br>{{cv.telephone}} <br>{{cv.profilCv}}  </p>

<button  "id="editButton" (click)="onEditClick(editButton)">Éditer</button>

<app-formmcv *ngIf="cv && isEditing" [cv]="cv"></app-formmcv>
`,
styles: [
]
})
export class EditcvComponent implements OnInit {
  isEditing: boolean = false;
 cv: Cv|undefined;
  constructor(
    private route: ActivatedRoute,
    private cvService: CvService,
  )
  {}

  @HostListener('click', ['$event.target']) onEditClick(target: HTMLElement) {
    if (target.id === 'editButton') {
      this.isEditing = true;
    }
  }

  ngOnInit() {
    const cvId: string|null = this.route.snapshot.paramMap.get('id');

    if (cvId) {
      this.cvService.getCVbyId(+cvId).subscribe(cv=>  this.cv = cv);
    }
    else {
      this.cv = undefined;
    }}}
