import { Component, Input} from '@angular/core';
import { ActivatedRoute} from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-editcv',
  template: `<h2 class="center" *ngIf="cv">Editer {{cv.id}}</h2>

  <p  class= "center">
    {{cv?.titCV}}
  </p>
 <app-formmcv *ngIf="cv" [cv]="cv"></app-formmcv>
`,
})
export class EditcvComponent {
  cv!: Cv|undefined
  constructor(
    private route: ActivatedRoute,
    private cvService: CvService,
  ){}



  ngOnInit() {
    const cvId: string|null = this.route.snapshot.paramMap.get('id');
    console.log(cvId)
    if(cvId){
      this.cvService.get(+cvId,data:).subscribe({next:(cv) => {this.cv = cv}} );
      console.log(this.cv?.id)
    }
  }

  }
