import { Component, Input, OnInit } from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';
@Component({
  selector: 'app-formmcv',
  templateUrl: './formmcv.component.html',
  styleUrls: ['./formmcv.component.css']
})
export class FormmcvComponent implements OnInit {
  @Input() cv!:Cv
  cvForm!: FormGroup;
  isAddCv!: boolean;
  loading = false;
  submitted = false;

  constructor(private formBuilder: FormBuilder,
    private router: Router,
    private cvService: CvService,
    private route: ActivatedRoute,) { 
      this.cvForm = this.formBuilder.group({
        nom: new FormControl(null, Validators.required) ,
        prenom: new FormControl(null, Validators.required),
        email: new FormControl  (null, [Validators.required, Validators.email]),
        telephone:new FormControl(null, [Validators.required, this.validateTelephoneFormat]),
        titCV: new FormControl(null, Validators.required),
        profilCv: new FormControl(null, Validators.required),
        objecCarri: new FormControl('', [Validators.required]]),
      });
       } 
  ngOnInit() {
    this.isAddCv=this.router.url.includes('add')
  }
  validateTelephoneFormat(control: AbstractControl): { [key: string]: boolean } | null {
    const phoneNumberPattern = /^\d{2}-\d{3}-\d{3}$/;
    if (control.value && !phoneNumberPattern.test(control.value)) {
      return { 'telephoneFormat': true };
    }
    return null;
  }  
  Submit() {
    if (this.isAddCv) {
      this.cvService.create(this.cv)   
      .subscribe((cv:Cv)=>this.router.navigate(['/cv',cv.id]))      
    }
    else {
      this.cvService.update(this.cv).subscribe(()=>this.router.navigate(['/cv',this.cv.id]),
      ); 
    this.router.navigate(['/cv', this.cv.id])
  } 
    }

