import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Cv } from '../models/cv.model';
const baseUrl = 'http://localhost:8080/api/cvs';
var headers_object = new HttpHeaders({
  'Content-Type': 'application/json',
});
const httpOptions = {
  headers: headers_object
};

@Injectable({
  providedIn: 'root'
})

export class CvService {

  constructor(private http: HttpClient) { }
  getAll(): Observable<Cv[]> {
    return this.http.get<Cv[]>(baseUrl);
  }
  get(id: any): Observable<Cv> {
    return this.http.get(`${baseUrl}/edit/${id}`);
  }

  create(data: any): Observable<any> {
    return this.http.post(baseUrl, data);
  }

  update( data: Cv): Observable<Cv> {
    return this.http.put(`${baseUrl}/${data.id}`,data,httpOptions);
  }


  delete(id: any): Observable<any> {
    return this.http.delete(`${baseUrl}/${id}`);
  }

  getCvById(id: any): Observable<Cv|undefined> {
    return this.http.get<Cv>(`${baseUrl}?id=${id}`);
  }




}
