import { Component, Input } from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormControlName, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-editform',
  templateUrl: './editform.component.html',
  styleUrls: ['./editform.component.css']
})
export class EditformComponent {

  cvForm!: FormGroup;
  cv: Cv|undefined;
  constructor(private formBuilder: FormBuilder,
    private router: Router,
    private cvService: CvService,
    private route: ActivatedRoute,) { 
      this.cvForm = this.formBuilder.group({
        nom: new FormControl ('', Validators.required) ,
        prenom:new FormControl('', Validators.required),
      });
       } 


       ngOnInit() {
        const cvId: string|null = this.route.snapshot.paramMap.get('id');

        if (cvId) {
          this.cvService.getCVbyId(+cvId).subscribe(cv=>  this.cv = cv);
        }
        else {
          this.cv = undefined;
        }}
      Submit() {
         this.cvService.update(this.cv, this.cv?.id).subscribe(()=>this.router.navigate(['/cv',this.cv?.id]),
      ); 
       this.router.navigate(['/cv', this.cv?.id])

      }




}
