import { Component, Input } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-editcv',
  template: `  <h2 class="center">Editer {{ cv?.nom}}</h2>
  <p *ngIf="cv" class= "center">
    {{cv.titCV}}
  </p>
 <app-cv-form *ngIf="cv" [cv]="cv"></app-cv-form>
`,
})
export class EditcvComponent {
  cv: Cv | undefined;
  constructor(
    private route: ActivatedRoute,
    private cvService: CvService,
    private router: Router
  ){ }



  ngOnInit() {
    const cvId: string|null = this.route.snapshot.paramMap.get('id');
    if(cvId){
      this.cvService.getCvById(+cvId).subscribe({next:(cv) => {this.cv = cv}} );
    }else{
      this.cv = undefined;
    }
  }

  }
