import { Component,HostListener } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-detailcv',
  templateUrl: './detailcv.component.html',
  styleUrls: ['./detailcv.component.css']
})

export class DetailcvComponent {
  cv!: Cv| undefined; 
  isEditing: boolean = false;
  constructor(
    private route: ActivatedRoute, 
    private cvService: CvService, 
    private router: Router) {}
    @HostListener('click', ['$event.target']) onEditClick(target: HTMLElement) {
      if (target.id === 'editButton') {
        this.isEditing = true;
      }
    }

  ngOnInit() {
    const cvId: string|null= this.route.snapshot.paramMap.get('id');
    if(cvId){
      this.cvService.getCVbyId(+cvId).subscribe(cv => this.cv = cv );
    }} 

  goToListCv(){
    this.router.navigate(['/list'])
  }

  deleteCV(cv:Cv){
 this.cvService.delete(cv.id)
      .subscribe(()=>this.goToListCv());}


      goToEditCv(cv: Cv){
        this.router.navigate(['/edit/cv', cv.id])
      }

  }
