import { MatDialog } from '@angular/material/dialog';
import { ActivatedRoute, Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-formmcv',
  templateUrl: './formmcv.component.html',
  styleUrls: ['./formmcv.component.css']
})
export class FormmcvComponent {
  @Input() cv!: Cv;
  cvForm!: FormGroup;
  isAddCv!: boolean;
  isEditCv!:boolean;
  selectedDomaine!: string;
  selectedSecteur!: string | null;
  filteredSecteurs: string[] = [];
  selectedCountry!: string;
  selectedRegion!: string;
  countries: string[] = ['Tunisie', 'France','Allemagne'];
  regions: string[] = ['Berlin', 'Lyon', 'Nice', 'Versailles', 'Tours', 'Paris', 'Toulouse','Strasbourg', 'Nantes', 'Tunis', 'Siliana', 'Jendouba', 'Kef', 'Sfax', 'Beja', 'Sousse', 'Monastir', 'Zaghouan', 'Nabeul', 'Munich', 'Hambourg', 'Francfort', 'Cologne' ];
  options: string[] = ['Temps plein', 'Temps partiel']; 
  selectedOption: string = ''; 
  contrat: string[] =['CDI','CDD','Freelance','Alternance'];
  selectedContrat: string = ''; 
  exForm!: FormGroup ;
  filteredConnaissances: string[] = [];
  connaissancesList: any[] = [];
  id: any;
  message = '';

  constructor(private formBuilder: FormBuilder,private router: Router,private dialog: MatDialog,private cvService: CvService,private route: ActivatedRoute,) { 
    this.cvForm = this.formBuilder.group({
      photoCv: [''],
      nom: ['', Validators.required],
      prenom: ['', Validators.required],
      email:  ['', [Validators.required, Validators.email]],
      telephone: ['', [Validators.required, this.validateTelephoneFormat]],
      titCV: ['', Validators.required],
      profilCv: ['', Validators.required],
      objecCarri: ['', [Validators.required]],
    });
  }

  ngOnInit() {
    this.message = '';
    this.isAddCv=this.router.url.includes('add')
    this.isEditCv=this.router.url.includes('edit')

  }
  validateTelephoneFormat(control: AbstractControl): { [key: string]: boolean } | null {
    const phoneNumberPattern = /^\d{2}-\d{3}-\d{3}$/;
    if (control.value && !phoneNumberPattern.test(control.value)) {
      return { 'telephoneFormat': true };
    }
    return null;
  }  
  Submit() {
    this.cv = this.cvForm.value;
    if (this.isAddCv) {
      this.cvService.create(this.cv).subscribe({
        next:(res)=>{
          console.log(res);


          this.router.navigate(['/']);

        },
        error: (e) => console.error(e)
      })    
    }

    else if(this.isEditCv)  {
      this.cvService.update(this.cv).subscribe(()=>{
      {this.router.navigate(['/cv', this.cv.id])}
      });

    }

  }

  avatarPreview: any | undefined;
  onFileSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        this.avatarPreview = reader.result as any;
        this.cvForm.controls['photoCv'].setValue(this.avatarPreview);
      };
    }
  }

}
<h1 *ngIf="isAddCv">C CV</h1>
<h1 *ngIf="!isAddCv">Edit CV</h1>


<form name="myForm" *ngIf="cv"
  [formGroup]="cvForm" (ngSubmit)="Submit()"
  class="dimention"
  style="margin-top: 100px; margin-bottom: 100px">
  <mat-card class="avatar-label" (click)="avatarInput.click()">
    <img *ngIf="avatarPreview" [src]="avatarPreview" alt="Avatar" />
    <mat-icon class="avatar-icon" *ngIf="!avatarPreview"
      ><img src="../../../assets/téléchargement.png"
    /></mat-icon>
  </mat-card> 
  <input
    type="file"
    id="avatar"
    #avatarInput
    style="display: none"
    (change)="onFileSelected($event)"
    accept="image/*"
  /> 

  <mat-card>
    <mat-card-content>
      <h5><b>Titre de CV</b></h5>
      <p>Ajouter un titre à votre CV en trois mots maximum</p>
      <mat-form-field >

        <input
          matInput
          placeholder="ex : Développeur Full Stack"
          formControlName="titCV" required 
        />   
        <mat-error *ngIf="cvForm.get('titCV')?.hasError('required')">Le titre de cv est requis</mat-error>
      </mat-form-field>
    </mat-card-content>
  </mat-card>

  <mat-card>
    <mat-form-field>
      <input matInput placeholder="Prenom" required formControlName="prenom"/>
      <mat-error *ngIf="cvForm.get('prenom')?.hasError('required')">Le prenom est requis</mat-error>

    </mat-form-field>

    <mat-form-field>
      <input matInput placeholder="Nom" required formControlName="nom" />
      <mat-error *ngIf="cvForm.get('nom')?.hasError('required')">Le nom est requis</mat-error>

    </mat-form-field>

    <mat-form-field>
      <mat-icon>email</mat-icon>
      <input matInput placeholder="Email"  required formControlName="email" type="email"/>
      <mat-error *ngIf="cvForm.get('email')?.hasError('required')">L'email est requis</mat-error>
      <mat-error *ngIf="cvForm.get('email')?.hasError('email')">L'email n'est pas valide</mat-error>
    </mat-form-field>

    <mat-form-field>
      <mat-icon>phone</mat-icon>
      <input
        matInput
        placeholder="99-999-999"
        formControlName="telephone" required 
      />
      <mat-error *ngIf="cvForm.get('telephone')?.hasError('required')">Le numéro de téléphone est requis</mat-error>
    <mat-error *ngIf="cvForm.get('telephone')?.hasError('telephoneFormat')">Le numéro de téléphone  doit etre sous la forme : 99-999-999</mat-error>
    </mat-form-field>
  </mat-card>

  <div class="profile-container">
    <mat-card>
      <mat-card-content>
        <h5><b>Description de profil</b></h5>
        <p>
          Résumez votre profil technique en quelques mots-clés pour aider les
          recruteurs à vous trouver
        </p>
        <mat-form-field>
          <textarea
            matInput
            placeholder="ex : Mastère télédétéction en géomatique"
            formControlName="profilCv"
            id="profilCv" required
          >

        </textarea>
        <mat-error *ngIf="cvForm.get('profilCv')?.hasError('required')">Le profil est requis</mat-error>
        </mat-form-field>
      </mat-card-content>
    </mat-card>
    <div class="profile-image">
      <img
        src="../../../assets/titre.jpg"
        alt="Image de profil"
        style="width: 300px; height: 230px; margin-left: 10px"
      />
    </div>
  </div>

  <mat-card>
    <mat-card-title style="margin-left: 20px"
      ><h5><b>Objectifs de carrière</b></h5></mat-card-title
    >
    <mat-card-content>
      <mat-form-field>
        <textarea
          matInput
          placeholder="Description"
          formControlName="objecCarri" required
        ></textarea> 
        <mat-error *ngIf="cvForm.get('objecCarri')?.hasError('required')">L' objecCarri est requis.</mat-error>

      </mat-form-field>
    </mat-card-content>
  </mat-card>
  <button
    mat-raised-button style="background-color: #f59a32; color: #020005" type="submit"[disabled]="cvForm.invalid" >
    Enregistrer et Contunier
  </button>
</form>
