import { Component, Input, OnInit} from '@angular/core';
import { ActivatedRoute, Router} from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-editcv',
  template: `
  <h1 class="center">Modifier CV {{cv?.nom}}</h1>
  <p>{{cv?.titCV}}</p>
  <app-formmcv *ngIf="cv" [cv]="cv"></app-formmcv>
`,
})
export class EditcvComponent implements OnInit {
  iseditCv!: boolean;
  cv!: Cv|undefined;
  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private cvService: CvService,
  )
  {
    this.iseditCv=this.router.url.includes('edit')
  }

  ngOnInit() {
    const cvId: string|null = this.route.snapshot.paramMap.get('id');
    if (this.iseditCv) {
    if (cvId) {
      this.cvService.get(+cvId).subscribe((data) => {if(cvId){ this.cv = data
        console.log("le cv à modifier est ", this.cv)}

      });
    }}}}
