import { Component, Input, OnInit } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { ActivatedRoute, Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';
import { first } from 'rxjs/operators';
@Component({
  selector: 'app-formmcv',
  templateUrl: './formmcv.component.html',
  styleUrls: ['./formmcv.component.css']
})
export class FormmcvComponent implements OnInit {
  @Input() cv!: Cv;
  cvForm!: FormGroup;
  isAddCv!: boolean;
  loading = false;
  submitted = false;
  id: any;
  iseditCv!: boolean;

  // selectedDomaine!: string;
  // selectedSecteur!: string | null;
  // filteredSecteurs: string[] = [];
  // selectedCountry!: string;
  // selectedRegion!: string;
  // countries: string[] = ['Tunisie', 'France','Allemagne'];
  // regions: string[] = ['Berlin', 'Lyon', 'Nice', 'Versailles', 'Tours', 'Paris', 'Toulouse','Strasbourg', 'Nantes', 'Tunis', 'Siliana', 'Jendouba', 'Kef', 'Sfax', 'Beja', 'Sousse', 'Monastir', 'Zaghouan', 'Nabeul', 'Munich', 'Hambourg', 'Francfort', 'Cologne' ];
  // options: string[] = ['Temps plein', 'Temps partiel']; 
  // selectedOption: string = ''; 
  // contrat: string[] =['CDI','CDD','Freelance','Alternance'];
  // selectedContrat: string = ''; 
  // exForm!: FormGroup ;
  // filteredConnaissances: string[] = [];
  // connaissancesList: any[] = [];

  constructor(private formBuilder: FormBuilder,
    private router: Router,
    private dialog: MatDialog,
    private cvService: CvService,
    private route: ActivatedRoute,) { this.iseditCv=this.router.url.includes('edit') }


  ngOnInit() {
    this.id = this.route.snapshot.params['id'];
    this.isAddCv=this.router.url.includes('add')
    this.cvForm = this.formBuilder.group({
      photoCv: [''],
      nom: ['', Validators.required],
      prenom: ['', Validators.required],
      email:  ['', [Validators.required, Validators.email]],
      telephone: ['', [Validators.required, this.validateTelephoneFormat]],
      titCV: ['', Validators.required],
      profilCv: ['', Validators.required],
      objecCarri: ['', [Validators.required]],
    });

  }
  validateTelephoneFormat(control: AbstractControl): { [key: string]: boolean } | null {
    const phoneNumberPattern = /^\d{2}-\d{3}-\d{3}$/;
    if (control.value && !phoneNumberPattern.test(control.value)) {
      return { 'telephoneFormat': true };
    }
    return null;
  }  
  Submit() {
    this.cv = this.cvForm.value;
    if (this.isAddCv) {
      this.cvService.create(this.cv)

      .subscribe({
        next:(res)=>{
          console.log(res);


          this.router.navigate(['/']);

        },
        error: (e) => console.error(e)
      })    
    }

    else {
      this.cvService.update(this.id,this.cvForm.value).subscribe(({
        next: (res) => {
          console.log(res);
        },
        error: (e) => console.error(e)
      }));

    }

  }

  // avatarPreview: any | undefined;
  // onFileSelected(event: any) {
  //   const file = event.target.files[0];
  //   if (file) {
  //     const reader = new FileReader();
  //     reader.readAsDataURL(file);
  //     reader.onload = () => {
  //       this.avatarPreview = reader.result as any;
  //       this.cvForm.controls['photoCv'].setValue(this.avatarPreview);
  //     };
  //   }
  // }

}
