import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-listcv',
  templateUrl: './listcv.component.html',
  styleUrls: ['./listcv.component.css']
})
export class ListcvComponent {
  Cv: any[] = [];

  constructor(private cvService: CvService,private router:Router) {}

  ngOnInit() {
    this.fetchCVs();
  }

  fetchCVs(): void {
    this.cvService.getAll()
      .subscribe({
        next: (data) => {
          this.Cv = data;
          console.log(data);
        },
        error: (e) => console.error(e)
      });
  }
  goToCv(cv: Cv){
    this.router.navigate(['/cv', cv.id])
   }
  }


<mat-grid-list cols="3" gutterSize="2" >
  <mat-grid-tile *ngFor="let cv of Cv" class="cv-container">
    <mat-card class="cv-card" style="background-color:#E6E0ED; margin-bottom: auto; margin-top: 10px; padding: 50px; mar" (click)="goToCv(cv)" >
      <!-- <img mat-card-image class="profile-image" [src]="cv.photoCv" alt="Photo de profil"> -->

      <mat-card-header>
        <mat-card-title>{{ cv.titCV }}</mat-card-title>
        <mat-card-subtitle>{{ cv.email }}</mat-card-subtitle>
      </mat-card-header>
      <mat-card-content>
        <h3>{{ cv.prenom }} {{ cv.nom }}</h3>
        <p><strong>Téléphone:</strong> {{ cv.telephone }}</p>
        <p><strong>Profil:</strong> {{ cv.profilCv }}</p>

      </mat-card-content>
    </mat-card>
  </mat-grid-tile>
</mat-grid-list>

<mat-fab
  class="mat-elevation-z8 rounded-fab"
  style="position: fixed; bottom: 25px; right: 25px; background-color:#9980B2 ;"
  [routerLink]="['/add/']"
  color="primary"
>
  <mat-icon>add</mat-icon>
</mat-fab>
