import { Component} from '@angular/core';
import { ActivatedRoute} from '@angular/router';
import { Cv } from 'src/app/models/cv.model';
import { CvService } from 'src/app/service/cv.service';

@Component({
  selector: 'app-editcv',
  template: `<h2 class="center"      const cvId: string|null = this.route.snapshot.paramMap.get('id');
>Editer {{ cv?.nom}}</h2>
  <p  class= "center">
    {{cv?.titCV}}
  </p>
 <app-formmcv *ngIf="cv"></app-formmcv>
`,
})
export class EditcvComponent {
  cv!: Cv|undefined
  constructor(
    private route: ActivatedRoute,
    private cvService: CvService,
  ){}



  ngOnInit() {
    const cvId: string|null = this.route.snapshot.paramMap.get('id');
    if(cvId){
      this.cvService.getCvById(+cvId).subscribe({next:(cv) => {this.cv = cv}} );
    }
  }

  }
